import React, { useState } from 'react';

function App() {
  const [jwt, setJwt] = useState('');
  const [secret, setSecret] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const analyzeJWT = async () => {
    setError('');
    setResult(null);
    try {
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ jwt, secret }),
      });
      const data = await response.json();
      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || 'Error desconocido');
      }
    } catch (e) {
      setError('No se pudo conectar con el backend.');
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h1>JWT Parse & Validator</h1>
      <input
        type="text"
        placeholder="Pega tu JWT aquÃ­"
        value={jwt}
        onChange={e => setJwt(e.target.value)}
        style={{ width: '100%', marginBottom: 10 }}
      />
      <input
        type="text"
        placeholder="Clave secreta (opcional)"
        value={secret}
        onChange={e => setSecret(e.target.value)}
        style={{ width: '100%', marginBottom: 10 }}
      />
      <button onClick={analyzeJWT}>Analizar JWT</button>
      {error && (
        <div style={{ color: 'red', marginTop: 20 }}>{error}</div>
      )}
      {result && (
        <div style={{ marginTop: 20 }}>
          <h2>Resultado</h2>
          <pre style={{ background: '#eee', padding: 10 }}>
            {JSON.stringify(result, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;
