import React, { useState } from 'react';

const pretty = obj =>
  typeof obj === 'string' ? obj : JSON.stringify(obj, null, 2);

function App() {
  const [jwt, setJwt] = useState('');
  const [secret, setSecret] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const analyzeJWT = async () => {
    setError('');
    setResult(null);
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ jwt, secret }),
      });
      const data = await response.json();
      setLoading(false);
      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || 'Error desconocido');
      }
    } catch (e) {
      setLoading(false);
      setError('No se pudo conectar con el backend.');
    }
  };

  return (
    <div style={{
      maxWidth: "680px", margin: "40px auto", background: "#fff",
      borderRadius: "16px", boxShadow: "0 4px 16px #ccc", padding: "32px", fontFamily: "Segoe UI"
      }}>
      <h1 style={{textAlign: "center", fontWeight: 700, letterSpacing: "2px", marginBottom: "40px"}}>üîê JWT Analyzer</h1>
      
      <label style={{fontWeight: 500}}>Token JWT</label>
      <textarea
        rows={3}
        className="input"
        placeholder="Pega tu JWT aqu√≠"
        style={{ width: "100%", fontSize: "16px", marginBottom: 14, borderRadius: 6, border: "1px solid #ccc", padding: 10 }}
        value={jwt}
        onChange={e => setJwt(e.target.value)}
      />
      <label style={{fontWeight: 500}}>Clave secreta (opcional para verificaci√≥n de firma)</label>
      <input
        type="text"
        className="input"
        style={{ width: "100%", fontSize: "16px", marginBottom: 20, borderRadius: 6, border: "1px solid #ccc", padding: 10 }}
        placeholder="Clave secreta"
        value={secret}
        onChange={e => setSecret(e.target.value)}
      />

      <button
        onClick={analyzeJWT}
        style={{
          fontWeight: 600, fontSize: "16px", padding: "8px 20px",
          background: "#313cff", color: "#fff", border: "none", borderRadius: 8,
          boxShadow: "0 2px 4px #ccc", cursor: "pointer", marginBottom: 8
        }}
      >
        {loading ? "Analizando..." : "Analizar JWT"}
      </button>
      
      {error && (
        <div style={{ color: "#ba0034", marginTop: 12, fontWeight: 600, fontSize: "16px" }}>{error}</div>
      )}

      {result && (
        <div style={{ marginTop: 28 }}>
          <h2 style={{ marginBottom: 10, color: "#313cff" }}>Resultado del an√°lisis</h2>

          <div style={{marginBottom: 20}}>
            <span style={{fontWeight: 600, color: "#1b8700"}}>
              <span>‚úì Estructura</span>: {result.estructura_valida ? "V√°lida" : <span style={{color:"#ba0034"}}>Inv√°lida</span>}
            </span>
          </div>

          <div style={{marginBottom: 10}}>
            <strong>Header decodificado:</strong>
            <pre style={{background:"#f2f6ff",padding:10, borderRadius:5}}>{pretty(result.header)}</pre>
          </div>
          <div style={{marginBottom: 10}}>
            <strong>Payload decodificado:</strong>
            <pre style={{background:"#f2f6ff",padding:10, borderRadius:5}}>{pretty(result.payload)}</pre>
          </div>
          <div style={{marginBottom: 10}}>
            <strong>√Årbol de derivaci√≥n:</strong>
            <pre style={{background:"#f9f9fa",padding:10, borderRadius:5, fontSize:"15px"}}>{result.arbol_derivacion}</pre>
          </div>
          
          <div style={{marginBottom:10}}>
            <strong>Claims (payload):</strong>
            <ul>
              {result.payload && typeof result.payload === 'object' && Object.keys(result.payload)
                .map(claim => (
                  <li key={claim}><b>{claim}</b>: {JSON.stringify(result.payload[claim])}</li>
                ))}
            </ul>
          </div>
          
          <div style={{marginBottom: 10}}>
            <strong>Validaci√≥n sem√°ntica:</strong>
            <div>
              <b>Errores:</b>
              <ul style={{ color: "#ba0034" }}>
                {result.errores.length ?
                  result.errores.map((err, idx) => <li key={idx}>{err}</li>)
                  : <li style={{color: "#1b8700"}}>Sin errores.</li>
                }
              </ul>
            </div>
            <div>
              <b>Warnings:</b>
              <ul style={{ color: "#f7a900" }}>
                {result.warnings.length ?
                  result.warnings.map((warn, idx) => <li key={idx}>{warn}</li>)
                  : <li style={{color: "#1b8700"}}>Sin advertencias.</li>
                }
              </ul>
            </div>
          </div>
          
          {typeof result.firma_valida !== "undefined" && (
            <div style={{marginBottom:10, fontWeight:500}}>
              <span>Verificaci√≥n de firma:</span>
              {result.firma_valida ? <span style={{ color: "#1b8700" }}>‚úì V√°lida</span> : <span style={{ color: "#ba0034" }}>‚úó Inv√°lida</span>}
            </div>
          )}
        </div>
      )}

      <div style={{marginTop:38, fontSize:"14px", color:"#777"}}>
        <b>Pautas validadas:</b> Estructura, decodificaci√≥n Base64URL, parsing JSON, visualizaci√≥n de claims, validaci√≥n sem√°ntica, verificaci√≥n de firma, manejo de errores.
      </div>
    </div>
  );
}

export default App;
